import './App.css';
import Table from './components/Table';
import data from './data/Wine-Data.json'

function App() {
  const alcoholList = data.reduce((result,alcohol)=>{
    const alcoholClass = alcohol.Alcohol
    if(!result[alcoholClass]){
      result[alcoholClass] =[]
    }
    result[alcoholClass].push(alcohol)
    return result
  },{})
  //console.log(alcoholList)
  function getMean (keyprop,index){
    let result
    Object.entries(alcoholList).map((key)=>{
      if(key[0]===keyprop){
        let ap = key[1].map((alcohol)=>{
          return alcohol.Flavanoids
        })

        result = ap.reduce((result,ap)=>{
          if (ap[0]==="."){
            ap =Number( 0 + ap)
          }
          result+=ap
          return result
        })
      }

    })
    return result.toFixed(3)
  }
  function getMedian (keyprop,index){
    let result
    Object.entries(alcoholList).map((key)=>{
      if(key[0]===keyprop){
        let ap = key[1].map((alcohol)=>{
          return alcohol.Flavanoids
        })
        let sortedAP = ap.sort((a,b)=>a-b)
        const middleValue = Math.floor(sortedAP.length / 2);
        if(sortedAP.length %2 ===0){
          result = (sortedAP[middleValue] + sortedAP[middleValue -1])/2
        } else{
          result = sortedAP[middleValue];
        }
      }
    })
    return result.toFixed(3)
  }
  function getMode (keyprop,index){
    let result = {}
    let maximumVal =0
    let count = 0
    Object.entries(alcoholList).map((key)=>{
      if(key[0]===keyprop){
        let ap = key[1].map((alcohol)=>{
          return alcohol.Flavanoids
        })
        for(let i=0;i<ap.length;i++){
          const number = ap[i]
          if(result[number]){
            result[number]++
          } else{
            result[number] = 1
          }
          if(count < result[number]){
            maximumVal = number
            count = result[number];
          } 
        }


      }
    })
    return maximumVal.toFixed(3)
  }
  return (
    <div className="App">
      <h1>Wine Data</h1>
      <Table alcoholList = {alcoholList}/>
    </div>
  );
}

export default App;
